    return SWITCHBOARD_ID


@view
@internal
def _getSwitchboardAddr() -> address:
    return staticcall UndyHq(UNDY_HQ_FOR_ADDYS).getAddr(SWITCHBOARD_ID)


# hatchery


@view
@internal
def _getHatcheryId() -> uint256:
    return HATCHERY_ID


@view
@internal
