contracts/modules/LocalGov.vy.content:
#     Underscore Protocol License: https://github.com/underscore-finance/underscore-protocol/blob/master/LICENSE.md

# @version 0.4.3

interface UndyHq:
    def minGovChangeTimeLock() -> uint256: view
    def maxGovChangeTimeLock() -> uint256: view
    def governance() -> address: view

struct PendingGovernance:
    newGov: address
    initiatedBlock: uint256
    confirmBlock: uint256

event GovChangeStarted:
    prevGov: indexed(address)
    newGov: indexed(address)
    confirmBlock: uint256

event GovChangeConfirmed:
    prevGov: indexed(address)
    newGov: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256

event GovChangeCancelled:
    cancelledGov: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256

event GovRelinquished:
    prevGov: indexed(address)

event GovChangeTimeLockModified:
    prevTimeLock: uint256
    newTimeLock: uint256

event UndyHqSetupFinished:
    prevGov: indexed(address)
    newGov: indexed(address)
    timeLock: uint256

# governance
governance: public(address)
pendingGov: public(PendingGovernance)
numGovChanges: public(uint256)

# time lock
govChangeTimeLock: public(uint256)

# config
UNDY_HQ_FOR_GOV: immutable(address)
MIN_GOV_TIME_LOCK: immutable(uint256)
MAX_GOV_TIME_LOCK: immutable(uint256)


@deploy
def __init__(
    _undyHq: address,
    _initialGov: address,
    _minTimeLock: uint256,
    _maxTimeLock: uint256,
    _initialTimeLock: uint256,
):
    UNDY_HQ_FOR_GOV = _undyHq
    self.governance = _initialGov

    # undy hq
    if _undyHq == empty(address):
        assert _initialGov != empty(address) # dev: undy hq must have gov

    # local gov (department, other smart contracts)
    else:
        hqGov: address = staticcall UndyHq(_undyHq).governance()
        assert hqGov != empty(address) # dev: undy hq must have gov
        assert _initialGov != hqGov # dev: undy hq cannot set same gov

    # time locks
    minTimeLock: uint256 = _minTimeLock
    maxTimeLock: uint256 = _maxTimeLock
    if minTimeLock == 0 or maxTimeLock == 0:
        assert _undyHq != empty(address) # dev: need undy hq if no time locks
        minTimeLock = staticcall UndyHq(_undyHq).minGovChangeTimeLock()
        maxTimeLock = staticcall UndyHq(_undyHq).maxGovChangeTimeLock()

    # set min and max time locks
    assert minTimeLock < maxTimeLock # dev: invalid time lock
    assert minTimeLock != 0 and maxTimeLock != max_value(uint256) # dev: invalid time lock
    MIN_GOV_TIME_LOCK = minTimeLock
    MAX_GOV_TIME_LOCK = maxTimeLock

    # this contract is top level governance from Undy HQ -- not setting initial time lock during setup
    if _undyHq == empty(address):
        return

    # set initial time lock (for local gov)
    initialTimeLock: uint256 = max(minTimeLock, _initialTimeLock)
    assert self._setGovTimeLock(initialTimeLock) # dev: invalid time lock


@view
@external
def getUndyHqFromGov() -> address:
    return self._getUndyHqFromGov()


@view
@internal
def _getUndyHqFromGov() -> address:
    return UNDY_HQ_FOR_GOV


##############
# Gov Access #
##############


@view
@external
def canGovern(_addr: address) -> bool:
    return self._canGovern(_addr)


@view
@internal
def _canGovern(_addr: address) -> bool:
    if _addr == empty(address):
        return False
    return _addr in self._getGovernors()


@view
@external
def getGovernors() -> DynArray[address, 2]:
    return self._getGovernors()


@view
@internal
def _getGovernors() -> DynArray[address, 2]:
    governors: DynArray[address, 2] = []

    # local governance
    localGov: address = self.governance
    if localGov != empty(address):
        governors.append(localGov)

    # undy hq governance
    undyHq: address = UNDY_HQ_FOR_GOV
    if undyHq == empty(address):
        return governors

    hqGov: address = staticcall UndyHq(undyHq).governance()
    if hqGov != empty(address):
        governors.append(hqGov)

    return governors


######################
# Governance Changes #
######################


@view
@external
def hasPendingGovChange() -> bool:
    return self.pendingGov.confirmBlock != 0


@view
@internal
def _isUndyHq() -> bool:
    return UNDY_HQ_FOR_GOV == empty(address)


# start gov change


@external
def startGovernanceChange(_newGov: address):
    governors: DynArray[address, 2] = self._getGovernors()
    assert msg.sender in governors # dev: no perms

    # validation
    if _newGov != empty(address):
        assert _newGov not in governors # dev: invalid _newGov
        assert _newGov.is_contract # dev: _newGov must be a contract
    else:
        assert not self._isUndyHq() # dev: undy hq cannot set 0x0

    confirmBlock: uint256 = block.number + self.govChangeTimeLock
    self.pendingGov = PendingGovernance(
        newGov= _newGov,
        initiatedBlock= block.number,
        confirmBlock= confirmBlock,
    )
    log GovChangeStarted(prevGov=self.governance, newGov=_newGov, confirmBlock=confirmBlock)


# confirm gov change


@external
def confirmGovernanceChange():
    data: PendingGovernance = self.pendingGov
    assert data.confirmBlock != 0 and block.number >= data.confirmBlock # dev: time lock not reached

    # check permissions
    if data.newGov != empty(address):
        assert msg.sender == data.newGov # dev: only new gov can confirm
    else:
        assert self._canGovern(msg.sender) # dev: no perms
        assert not self._isUndyHq() # dev: undy hq cannot set 0x0

    # set new governance
    prevGov: address = self.governance
    self.governance = data.newGov
    self.numGovChanges += 1
    self.pendingGov = empty(PendingGovernance)
    log GovChangeConfirmed(prevGov=prevGov, newGov=data.newGov, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock)


# cancel gov change


@external
def cancelGovernanceChange():
    assert self._canGovern(msg.sender) # dev: no perms
    data: PendingGovernance = self.pendingGov
    assert data.confirmBlock != 0 # dev: no pending change
    self.pendingGov = empty(PendingGovernance)
    log GovChangeCancelled(cancelledGov=data.newGov, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock)


# relinquish gov (only for local gov)


@external
def relinquishGov():
    assert msg.sender == self.governance # dev: no perms
    assert not self._isUndyHq() # dev: undy hq cannot relinquish gov

    self.governance = empty(address)
    self.numGovChanges += 1
    log GovRelinquished(prevGov=msg.sender)


####################
# Time Lock Config #
####################


# set time lock


@external
def setGovTimeLock(_numBlocks: uint256) -> bool:
    assert self._canGovern(msg.sender) # dev: no perms
    return self._setGovTimeLock(_numBlocks)


@internal
def _setGovTimeLock(_numBlocks: uint256) -> bool:
    prevTimeLock: uint256 = self.govChangeTimeLock
    assert self._isValidGovTimeLock(_numBlocks, prevTimeLock) # dev: invalid time lock
    self.govChangeTimeLock = _numBlocks
    log GovChangeTimeLockModified(prevTimeLock=prevTimeLock, newTimeLock=_numBlocks)
    return True


# validation


@view
@external
def isValidGovTimeLock(_newTimeLock: uint256) -> bool:
    return self._isValidGovTimeLock(_newTimeLock, self.govChangeTimeLock)


@view
@internal
def _isValidGovTimeLock(_newTimeLock: uint256, _prevTimeLock: uint256) -> bool:
    if _newTimeLock == _prevTimeLock:
        return False # no change
    if self.pendingGov.confirmBlock != 0:
        return False # cannot change while pending gov change
    return _newTimeLock >= MIN_GOV_TIME_LOCK and _newTimeLock <= MAX_GOV_TIME_LOCK


# utils


@view
@external
def minGovChangeTimeLock() -> uint256:
    return MIN_GOV_TIME_LOCK


@view
@external
def maxGovChangeTimeLock() -> uint256:
    return MAX_GOV_TIME_LOCK


#################
# Undy Hq Setup #
#################


@external
def finishUndyHqSetup(_newGov: address, _timeLock: uint256 = 0) -> bool:
    assert self._isUndyHq() # dev: only undy hq
    assert msg.sender == self.governance # dev: no perms
    assert self.numGovChanges == 0 # dev: already changed gov

    # validation
    assert _newGov != empty(address) and _newGov.is_contract # dev: invalid _newGov
    prevGov: address = self.governance

    # set new gov
    self.governance = _newGov
    self.numGovChanges += 1

    # set time lock
    timeLock: uint256 = _timeLock
    if timeLock == 0:
        timeLock = MIN_GOV_TIME_LOCK
    assert self._setGovTimeLock(timeLock) # dev: invalid time lock

    log UndyHqSetupFinished(prevGov=prevGov, newGov=_newGov, timeLock=timeLock)
    return True

contracts/modules/TimeLock.vy.content:
#     Underscore Protocol License: https://github.com/underscore-finance/underscore-protocol/blob/master/LICENSE.md

# @version 0.4.3

uses: gov
import contracts.modules.LocalGov as gov

struct PendingAction:
    initiatedBlock: uint256
    confirmBlock: uint256
    expiration: uint256

event ActionTimeLockSet:
    newTimeLock: uint256
    prevTimeLock: uint256

event ExpirationSet:
    expiration: uint256

# core data
pendingActions: public(HashMap[uint256, PendingAction])
actionId: public(uint256)

# config
actionTimeLock: public(uint256)
expiration: public(uint256)

MIN_ACTION_TIMELOCK: immutable(uint256)
MAX_ACTION_TIMELOCK: immutable(uint256)


@deploy
def __init__(
    _minActionTimeLock: uint256,
    _maxActionTimeLock: uint256,
    _initialTimeLock: uint256,
    _expiration: uint256,
):
    # start at 1 index
    self.actionId = 1

    # set time lock boundaries
    assert _minActionTimeLock < _maxActionTimeLock # dev: invalid time lock boundaries
    assert _minActionTimeLock != 0 and _maxActionTimeLock != max_value(uint256) # dev: invalid time lock boundaries
    MIN_ACTION_TIMELOCK = _minActionTimeLock
    MAX_ACTION_TIMELOCK = _maxActionTimeLock

    # set expiration time
    self._setExpiration(_expiration, _initialTimeLock)

    # set initial time lock
    if _initialTimeLock != 0:
        assert self._setActionTimeLock(_initialTimeLock, 0) # dev: failed to set initial time lock


########
# Core #
########


# initiate


@internal
def _initiateAction() -> uint256:
    actionId: uint256 = self.actionId
    confirmBlock: uint256 = block.number + self.actionTimeLock
    self.pendingActions[actionId] = PendingAction(
        initiatedBlock= block.number,
        confirmBlock= confirmBlock,
        expiration= confirmBlock + self.expiration,
    )
    self.actionId += 1
    return actionId


# confirm


@internal
def _confirmAction(_actionId: uint256) -> bool:
    if not self._canConfirmAction(_actionId):
        return False
    self.pendingActions[_actionId] = empty(PendingAction)
    return True


# cancel


@internal
def _cancelAction(_actionId: uint256) -> bool:
    data: PendingAction = self.pendingActions[_actionId]
    if data.confirmBlock == 0:
        return False
    self.pendingActions[_actionId] = empty(PendingAction)
    return True


#########
# Utils #
#########


# can confirm


@view
@external
def canConfirmAction(_actionId: uint256) -> bool:
    return self._canConfirmAction(_actionId)


@view
@internal
def _canConfirmAction(_actionId: uint256) -> bool:
    data: PendingAction = self.pendingActions[_actionId]
    if data.confirmBlock == 0 or block.number < data.confirmBlock:
        return False
    if block.number >= data.expiration:
        return False
    return True


# is expired


@view
@external
def isExpired(_actionId: uint256) -> bool:
    return self._isExpired(_actionId)


@view
@internal
def _isExpired(_actionId: uint256) -> bool:
    data: PendingAction = self.pendingActions[_actionId]
    if data.confirmBlock == 0:
        return False
    return block.number >= data.expiration


# pending action


@view
@external
def hasPendingAction(_actionId: uint256) -> bool:
    return self._hasPendingAction(_actionId)


@view
@internal
def _hasPendingAction(_actionId: uint256) -> bool:
    return self.pendingActions[_actionId].confirmBlock != 0


# confirmation block


@view
@external
def getActionConfirmationBlock(_actionId: uint256) -> uint256:
    return self._getActionConfirmationBlock(_actionId)


@view
@internal
def _getActionConfirmationBlock(_actionId: uint256) -> uint256:
    return self.pendingActions[_actionId].confirmBlock


######################
# Config - Time Lock #
######################


@external
def setActionTimeLock(_newTimeLock: uint256) -> bool:
    assert gov._canGovern(msg.sender) # dev: no perms
    return self._setActionTimeLock(_newTimeLock, self.actionTimeLock)


@internal
def _setActionTimeLock(_newTimeLock: uint256, _prevTimeLock: uint256) -> bool:
    assert self._isValidActionTimeLock(_newTimeLock, _prevTimeLock) # dev: invalid time lock
    self.actionTimeLock = _newTimeLock
    log ActionTimeLockSet(newTimeLock=_newTimeLock, prevTimeLock=_prevTimeLock)
    return True


# validation


@view
@external
def isValidActionTimeLock(_newTimeLock: uint256) -> bool:
    return self._isValidActionTimeLock(_newTimeLock, self.actionTimeLock)


@view
@internal
def _isValidActionTimeLock(_newTimeLock: uint256, _prevTimeLock: uint256) -> bool:
    if _newTimeLock == _prevTimeLock:
        return False # no change
    return _newTimeLock >= MIN_ACTION_TIMELOCK and _newTimeLock <= MAX_ACTION_TIMELOCK


# utils


@view
@external
def minActionTimeLock() -> uint256:
    return MIN_ACTION_TIMELOCK


@view
@external
def maxActionTimeLock() -> uint256:
    return MAX_ACTION_TIMELOCK


#######################
# Config - Expiration #
#######################


@external
def setExpiration(_expiration: uint256) -> bool:
    assert gov._canGovern(msg.sender) # dev: no perms
    return self._setExpiration(_expiration, self.actionTimeLock)


@internal
def _setExpiration(_expiration: uint256, _timeLock: uint256) -> bool:
    assert self._isValidExpiration(_expiration, _timeLock) # dev: invalid expiration
    self.expiration = _expiration
    log ExpirationSet(expiration=_expiration)
    return True


# validation


@view
@internal
def _isValidExpiration(_expiration: uint256, _timeLock: uint256) -> bool:
    if _expiration == 0 or _expiration == max_value(uint256):
        return False
    if _expiration < _timeLock:
        return False
    return True


################
# Finish Setup #
################


@external
def setActionTimeLockAfterSetup(_newTimeLock: uint256 = 0) -> bool:
    assert gov._canGovern(msg.sender) # dev: no perms

    prevTimeLock: uint256 = self.actionTimeLock
    assert prevTimeLock == 0 # dev: already set

    timeLock: uint256 = _newTimeLock
    if timeLock == 0:
        timeLock = MIN_ACTION_TIMELOCK
    return self._setActionTimeLock(timeLock, prevTimeLock)
