contracts/modules/LocalGov.vy.content:
#     Underscore Protocol License: https://github.com/underscore-finance/underscore-protocol/blob/master/LICENSE.md

# @version 0.4.3

interface UndyHq:
    def minGovChangeTimeLock() -> uint256: view
    def maxGovChangeTimeLock() -> uint256: view
    def governance() -> address: view

struct PendingGovernance:
    newGov: address
    initiatedBlock: uint256
    confirmBlock: uint256

event GovChangeStarted:
    prevGov: indexed(address)
    newGov: indexed(address)
    confirmBlock: uint256

event GovChangeConfirmed:
    prevGov: indexed(address)
    newGov: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256

event GovChangeCancelled:
    cancelledGov: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256

event GovRelinquished:
    prevGov: indexed(address)

event GovChangeTimeLockModified:
    prevTimeLock: uint256
    newTimeLock: uint256

event UndyHqSetupFinished:
    prevGov: indexed(address)
    newGov: indexed(address)
    timeLock: uint256

# governance
governance: public(address)
pendingGov: public(PendingGovernance)
numGovChanges: public(uint256)

# time lock
govChangeTimeLock: public(uint256)

# config
UNDY_HQ_FOR_GOV: immutable(address)
MIN_GOV_TIME_LOCK: immutable(uint256)
MAX_GOV_TIME_LOCK: immutable(uint256)


@deploy
def __init__(
    _undyHq: address,
    _initialGov: address,
    _minTimeLock: uint256,
    _maxTimeLock: uint256,
    _initialTimeLock: uint256,
):
    UNDY_HQ_FOR_GOV = _undyHq
    self.governance = _initialGov

    # undy hq
    if _undyHq == empty(address):
        assert _initialGov != empty(address) # dev: undy hq must have gov

    # local gov (department, other smart contracts)
    else:
        hqGov: address = staticcall UndyHq(_undyHq).governance()
        assert hqGov != empty(address) # dev: undy hq must have gov
        assert _initialGov != hqGov # dev: undy hq cannot set same gov

    # time locks
    minTimeLock: uint256 = _minTimeLock
    maxTimeLock: uint256 = _maxTimeLock
    if minTimeLock == 0 or maxTimeLock == 0:
        assert _undyHq != empty(address) # dev: need undy hq if no time locks
        minTimeLock = staticcall UndyHq(_undyHq).minGovChangeTimeLock()
        maxTimeLock = staticcall UndyHq(_undyHq).maxGovChangeTimeLock()

    # set min and max time locks
    assert minTimeLock < maxTimeLock # dev: invalid time lock
    assert minTimeLock != 0 and maxTimeLock != max_value(uint256) # dev: invalid time lock
    MIN_GOV_TIME_LOCK = minTimeLock
    MAX_GOV_TIME_LOCK = maxTimeLock

    # this contract is top level governance from Undy HQ -- not setting initial time lock during setup
    if _undyHq == empty(address):
        return

    # set initial time lock (for local gov)
    initialTimeLock: uint256 = max(minTimeLock, _initialTimeLock)
    assert self._setGovTimeLock(initialTimeLock) # dev: invalid time lock


@view
@external
def getUndyHqFromGov() -> address:
    return self._getUndyHqFromGov()


@view
@internal
def _getUndyHqFromGov() -> address:
    return UNDY_HQ_FOR_GOV


##############
# Gov Access #
##############


@view
@external
def canGovern(_addr: address) -> bool:
    return self._canGovern(_addr)


@view
@internal
def _canGovern(_addr: address) -> bool:
    if _addr == empty(address):
        return False
    return _addr in self._getGovernors()


@view
@external
def getGovernors() -> DynArray[address, 2]:
    return self._getGovernors()


@view
@internal
def _getGovernors() -> DynArray[address, 2]:
    governors: DynArray[address, 2] = []

    # local governance
    localGov: address = self.governance
    if localGov != empty(address):
        governors.append(localGov)

    # undy hq governance
    undyHq: address = UNDY_HQ_FOR_GOV
    if undyHq == empty(address):
        return governors

    hqGov: address = staticcall UndyHq(undyHq).governance()
    if hqGov != empty(address):
        governors.append(hqGov)

    return governors


######################
# Governance Changes #
######################


@view
@external
def hasPendingGovChange() -> bool:
    return self.pendingGov.confirmBlock != 0


@view
@internal
def _isUndyHq() -> bool:
    return UNDY_HQ_FOR_GOV == empty(address)


# start gov change


@external
def startGovernanceChange(_newGov: address):
    governors: DynArray[address, 2] = self._getGovernors()
    assert msg.sender in governors # dev: no perms

    # validation
    if _newGov != empty(address):
        assert _newGov not in governors # dev: invalid _newGov
        assert _newGov.is_contract # dev: _newGov must be a contract
    else:
        assert not self._isUndyHq() # dev: undy hq cannot set 0x0

    confirmBlock: uint256 = block.number + self.govChangeTimeLock
    self.pendingGov = PendingGovernance(
        newGov= _newGov,
        initiatedBlock= block.number,
        confirmBlock= confirmBlock,
    )
    log GovChangeStarted(prevGov=self.governance, newGov=_newGov, confirmBlock=confirmBlock)


# confirm gov change


@external
def confirmGovernanceChange():
    data: PendingGovernance = self.pendingGov
    assert data.confirmBlock != 0 and block.number >= data.confirmBlock # dev: time lock not reached

    # check permissions
    if data.newGov != empty(address):
        assert msg.sender == data.newGov # dev: only new gov can confirm
    else:
        assert self._canGovern(msg.sender) # dev: no perms
        assert not self._isUndyHq() # dev: undy hq cannot set 0x0

    # set new governance
    prevGov: address = self.governance
    self.governance = data.newGov
    self.numGovChanges += 1
    self.pendingGov = empty(PendingGovernance)
    log GovChangeConfirmed(prevGov=prevGov, newGov=data.newGov, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock)


# cancel gov change


@external
def cancelGovernanceChange():
    assert self._canGovern(msg.sender) # dev: no perms
    data: PendingGovernance = self.pendingGov
    assert data.confirmBlock != 0 # dev: no pending change
    self.pendingGov = empty(PendingGovernance)
    log GovChangeCancelled(cancelledGov=data.newGov, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock)


# relinquish gov (only for local gov)


@external
def relinquishGov():
    assert msg.sender == self.governance # dev: no perms
    assert not self._isUndyHq() # dev: undy hq cannot relinquish gov

    self.governance = empty(address)
    self.numGovChanges += 1
    log GovRelinquished(prevGov=msg.sender)


####################
# Time Lock Config #
####################


# set time lock


@external
def setGovTimeLock(_numBlocks: uint256) -> bool:
    assert self._canGovern(msg.sender) # dev: no perms
    return self._setGovTimeLock(_numBlocks)


@internal
def _setGovTimeLock(_numBlocks: uint256) -> bool:
    prevTimeLock: uint256 = self.govChangeTimeLock
    assert self._isValidGovTimeLock(_numBlocks, prevTimeLock) # dev: invalid time lock
    self.govChangeTimeLock = _numBlocks
    log GovChangeTimeLockModified(prevTimeLock=prevTimeLock, newTimeLock=_numBlocks)
    return True


# validation


@view
@external
def isValidGovTimeLock(_newTimeLock: uint256) -> bool:
    return self._isValidGovTimeLock(_newTimeLock, self.govChangeTimeLock)


@view
@internal
def _isValidGovTimeLock(_newTimeLock: uint256, _prevTimeLock: uint256) -> bool:
    if _newTimeLock == _prevTimeLock:
        return False # no change
    if self.pendingGov.confirmBlock != 0:
        return False # cannot change while pending gov change
    return _newTimeLock >= MIN_GOV_TIME_LOCK and _newTimeLock <= MAX_GOV_TIME_LOCK


# utils


@view
@external
def minGovChangeTimeLock() -> uint256:
    return MIN_GOV_TIME_LOCK


@view
@external
def maxGovChangeTimeLock() -> uint256:
    return MAX_GOV_TIME_LOCK


#################
# Undy Hq Setup #
#################


@external
def finishUndyHqSetup(_newGov: address, _timeLock: uint256 = 0) -> bool:
    assert self._isUndyHq() # dev: only undy hq
    assert msg.sender == self.governance # dev: no perms
    assert self.numGovChanges == 0 # dev: already changed gov

    # validation
    assert _newGov != empty(address) and _newGov.is_contract # dev: invalid _newGov
    prevGov: address = self.governance

    # set new gov
    self.governance = _newGov
    self.numGovChanges += 1

    # set time lock
    timeLock: uint256 = _timeLock
    if timeLock == 0:
        timeLock = MIN_GOV_TIME_LOCK
    assert self._setGovTimeLock(timeLock) # dev: invalid time lock

    log UndyHqSetupFinished(prevGov=prevGov, newGov=_newGov, timeLock=timeLock)
    return True

contracts/modules/AddressRegistry.vy.content:
#     Underscore Protocol License: https://github.com/underscore-finance/underscore-protocol/blob/master/LICENSE.md

# @version 0.4.3

uses: gov
import contracts.modules.LocalGov as gov

struct AddressInfo:
    addr: address
    version: uint256
    lastModified: uint256
    description: String[64]

struct PendingNewAddress:
    description: String[64]
    initiatedBlock: uint256
    confirmBlock: uint256

struct PendingAddressUpdate:
    newAddr: address
    initiatedBlock: uint256
    confirmBlock: uint256

struct PendingAddressDisable:
    initiatedBlock: uint256
    confirmBlock: uint256

event NewAddressPending:
    addr: indexed(address)
    description: String[64]
    confirmBlock: uint256
    registry: String[28]

event NewAddressConfirmed:
    addr: indexed(address)
    regId: uint256
    description: String[64]
    registry: String[28]

event NewAddressCancelled:
    description: String[64]
    addr: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256
    registry: String[28]

event AddressUpdatePending:
    regId: uint256
    description: String[64]
    newAddr: indexed(address)
    prevAddr: indexed(address)
    version: uint256
    confirmBlock: uint256
    registry: String[28]

event AddressUpdateConfirmed:
    regId: uint256
    description: String[64]
    newAddr: indexed(address)
    prevAddr: indexed(address)
    version: uint256
    registry: String[28]

event AddressUpdateCancelled:
    regId: uint256
    description: String[64]
    newAddr: indexed(address)
    prevAddr: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256
    registry: String[28]

event AddressDisablePending:
    regId: uint256
    description: String[64]
    addr: indexed(address)
    version: uint256
    confirmBlock: uint256
    registry: String[28]

event AddressDisableConfirmed:
    regId: uint256
    description: String[64]
    addr: indexed(address)
    version: uint256
    registry: String[28]

event AddressDisableCancelled:
    regId: uint256
    description: String[64]
    addr: indexed(address)
    initiatedBlock: uint256
    confirmBlock: uint256
    registry: String[28]

event RegistryTimeLockModified:
    newTimeLock: uint256
    prevTimeLock: uint256
    registry: String[28]

# time lock
registryChangeTimeLock: public(uint256)

# core data (address registry)
addrInfo: public(HashMap[uint256, AddressInfo]) # regId -> address info
addrToRegId: public(HashMap[address, uint256]) # addr -> regId
numAddrs: public(uint256) # number of addrs in registry

# pending changes
pendingNewAddr: public(HashMap[address, PendingNewAddress]) # addr -> pending new addr
pendingAddrUpdate: public(HashMap[uint256, PendingAddressUpdate]) # regId -> pending addr update
pendingAddrDisable: public(HashMap[uint256, PendingAddressDisable]) # regId -> pending addr disable

REGISTRY_STR: immutable(String[28])
MIN_REG_TIME_LOCK: immutable(uint256)
MAX_REG_TIME_LOCK: immutable(uint256)


@deploy
def __init__(
    _minTimeLock: uint256,
    _maxTimeLock: uint256,
    _initialTimeLock: uint256,
    _registryStr: String[28],
):
    REGISTRY_STR = _registryStr

    assert _minTimeLock < _maxTimeLock # dev: invalid time lock
    assert _minTimeLock != 0 and _maxTimeLock != max_value(uint256) # dev: invalid time lock
    MIN_REG_TIME_LOCK = _minTimeLock
    MAX_REG_TIME_LOCK = _maxTimeLock

    # set initial time lock -- this may be zero during inital setup of registry
    if _initialTimeLock != 0:
        assert self._setRegistryTimeLock(_initialTimeLock) # dev: invalid time lock

    # start at 1 index
    self.numAddrs = 1


@view
@external
def getRegistryDescription() -> String[28]:
    return REGISTRY_STR


###############
# New Address #
###############


# register new address


@internal
def _startAddNewAddressToRegistry(_addr: address, _description: String[64]) -> bool:
    assert self._isValidNewAddress(_addr) # dev: invalid addy

    confirmBlock: uint256 = block.number + self.registryChangeTimeLock
    self.pendingNewAddr[_addr] = PendingNewAddress(
        description=_description,
        initiatedBlock=block.number,
        confirmBlock=confirmBlock,
    )

    log NewAddressPending(addr=_addr, description=_description, confirmBlock=confirmBlock, registry=REGISTRY_STR)
    return True


# confirm new address


@internal
def _confirmNewAddressToRegistry(_addr: address) -> uint256:
    data: PendingNewAddress = self.pendingNewAddr[_addr]
    assert data.confirmBlock != 0 and block.number >= data.confirmBlock # dev: time lock not reached

    if not self._isValidNewAddress(_addr):
        self.pendingNewAddr[_addr] = empty(PendingNewAddress) # clear pending
        return 0

    # register new addy
    regId: uint256 = self.numAddrs
    self.addrToRegId[_addr] = regId
    self.numAddrs = regId + 1
    self.addrInfo[regId] = AddressInfo(
        addr=_addr,
        version=1,
        lastModified=block.timestamp,
        description=data.description,
    )

    # clear pending
    self.pendingNewAddr[_addr] = empty(PendingNewAddress)

    log NewAddressConfirmed(addr=_addr, regId=regId, description=data.description, registry=REGISTRY_STR)
    return regId


# cancel new address


@internal
def _cancelNewAddressToRegistry(_addr: address) -> bool:
    data: PendingNewAddress = self.pendingNewAddr[_addr]
    assert data.confirmBlock != 0 # dev: no pending

    self.pendingNewAddr[_addr] = empty(PendingNewAddress)
    log NewAddressCancelled(description=data.description, addr=_addr, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock, registry=REGISTRY_STR)
    return True


# validation


@view
@external
def isValidNewAddress(_addr: address) -> bool:
    return self._isValidNewAddress(_addr)


@view
@internal
def _isValidNewAddress(_addr: address) -> bool:
    if _addr == empty(address) or not _addr.is_contract:
        return False
    return self.addrToRegId[_addr] == 0


###################
# Address Updates #
###################


# update address


@internal
def _startAddressUpdateToRegistry(_regId: uint256, _newAddr: address) -> bool:
    data: AddressInfo = self.addrInfo[_regId]
    assert self._isValidAddressUpdate(_regId, _newAddr, data.addr) # dev: invalid update

    # set pending
    confirmBlock: uint256 = block.number + self.registryChangeTimeLock
    self.pendingAddrUpdate[_regId] = PendingAddressUpdate(
        newAddr=_newAddr,
        initiatedBlock=block.number,
        confirmBlock=confirmBlock,
    )

    log AddressUpdatePending(regId=_regId, description=data.description, newAddr=_newAddr, prevAddr=data.addr, version=data.version, confirmBlock=confirmBlock, registry=REGISTRY_STR)
    return True


# confirm update address


@internal
def _confirmAddressUpdateToRegistry(_regId: uint256) -> bool:
    newData: PendingAddressUpdate = self.pendingAddrUpdate[_regId]
    assert newData.confirmBlock != 0 and block.number >= newData.confirmBlock # dev: time lock not reached

    data: AddressInfo = self.addrInfo[_regId]
    prevAddr: address = data.addr # needed for later
    if not self._isValidAddressUpdate(_regId, newData.newAddr, prevAddr):
        self.pendingAddrUpdate[_regId] = empty(PendingAddressUpdate) # clear pending
        return False

    # update addy data
    data.addr = newData.newAddr
    data.lastModified = block.timestamp
    data.version += 1
    self.addrInfo[_regId] = data
    self.addrToRegId[newData.newAddr] = _regId

    # handle previous addr
    if prevAddr != empty(address):
        self.addrToRegId[prevAddr] = 0

    # clear pending
    self.pendingAddrUpdate[_regId] = empty(PendingAddressUpdate)

    log AddressUpdateConfirmed(regId=_regId, description=data.description, newAddr=newData.newAddr, prevAddr=prevAddr, version=data.version, registry=REGISTRY_STR)
    return True


# cancel update address


@internal
def _cancelAddressUpdateToRegistry(_regId: uint256) -> bool:
    data: PendingAddressUpdate = self.pendingAddrUpdate[_regId]
    assert data.confirmBlock != 0 # dev: no pending

    self.pendingAddrUpdate[_regId] = empty(PendingAddressUpdate)
    prevData: AddressInfo = self.addrInfo[_regId]
    log AddressUpdateCancelled(regId=_regId, description=prevData.description, newAddr=data.newAddr, prevAddr=prevData.addr, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock, registry=REGISTRY_STR)
    return True


# validation


@view
@external
def isValidAddressUpdate(_regId: uint256, _newAddr: address) -> bool:
    return self._isValidAddressUpdate(_regId, _newAddr, self.addrInfo[_regId].addr)


@view
@internal
def _isValidAddressUpdate(_regId: uint256, _newAddr: address, _prevAddr: address) -> bool:
    if not self._isValidRegId(_regId):
        return False
    if not self._isValidNewAddress(_newAddr):
        return False
    return _newAddr != _prevAddr


###################
# Disable Address #
###################


# start disable address


@internal
def _startAddressDisableInRegistry(_regId: uint256) -> bool:
    data: AddressInfo = self.addrInfo[_regId]
    assert self._isValidAddressDisable(_regId, data.addr) # dev: invalid disable

    # set pending
    confirmBlock: uint256 = block.number + self.registryChangeTimeLock
    self.pendingAddrDisable[_regId] = PendingAddressDisable(
        initiatedBlock=block.number,
        confirmBlock=confirmBlock,
    )

    log AddressDisablePending(regId=_regId, description=data.description, addr=data.addr, version=data.version, confirmBlock=confirmBlock, registry=REGISTRY_STR)
    return True


# confirm disable address


@internal
def _confirmAddressDisableInRegistry(_regId: uint256) -> bool:
    newData: PendingAddressDisable = self.pendingAddrDisable[_regId]
    assert newData.confirmBlock != 0 and block.number >= newData.confirmBlock # dev: time lock not reached

    data: AddressInfo = self.addrInfo[_regId]
    prevAddr: address = data.addr # needed for later
    if not self._isValidAddressDisable(_regId, prevAddr):
        self.pendingAddrDisable[_regId] = empty(PendingAddressDisable) # clear pending
        return False

    # disable addy
    data.addr = empty(address)
    data.lastModified = block.timestamp
    data.version += 1
    self.addrInfo[_regId] = data
    self.addrToRegId[prevAddr] = 0

    # clear pending
    self.pendingAddrDisable[_regId] = empty(PendingAddressDisable)

    log AddressDisableConfirmed(regId=_regId, description=data.description, addr=prevAddr, version=data.version, registry=REGISTRY_STR)
    return True


# cancel disable address


@internal
def _cancelAddressDisableInRegistry(_regId: uint256) -> bool:
    data: PendingAddressDisable = self.pendingAddrDisable[_regId]
    assert data.confirmBlock != 0 # dev: no pending

    self.pendingAddrDisable[_regId] = empty(PendingAddressDisable)
    prevData: AddressInfo = self.addrInfo[_regId]
    log AddressDisableCancelled(regId=_regId, description=prevData.description, addr=prevData.addr, initiatedBlock=data.initiatedBlock, confirmBlock=data.confirmBlock, registry=REGISTRY_STR)
    return True


# validation


@view
@external
def isValidAddressDisable(_regId: uint256) -> bool:
    return self._isValidAddressDisable(_regId, self.addrInfo[_regId].addr)


@view
@internal
def _isValidAddressDisable(_regId: uint256, _prevAddr: address) -> bool:
    if not self._isValidRegId(_regId):
        return False
    return _prevAddr != empty(address)


####################
# Time Lock Config #
####################


@external
def setRegistryTimeLock(_numBlocks: uint256) -> bool:
    assert gov._canGovern(msg.sender) # dev: no perms
    return self._setRegistryTimeLock(_numBlocks)


@internal
def _setRegistryTimeLock(_numBlocks: uint256) -> bool:
    prevTimeLock: uint256 = self.registryChangeTimeLock
    assert self._isValidRegistryTimeLock(_numBlocks, prevTimeLock) # dev: invalid time lock
    self.registryChangeTimeLock = _numBlocks
    log RegistryTimeLockModified(newTimeLock=_numBlocks, prevTimeLock=prevTimeLock, registry=REGISTRY_STR)
    return True


# validation


@view
@external
def isValidRegistryTimeLock(_numBlocks: uint256) -> bool:
    return self._isValidRegistryTimeLock(_numBlocks, self.registryChangeTimeLock)


@view
@internal
def _isValidRegistryTimeLock(_newTimeLock: uint256, _prevTimeLock: uint256) -> bool:
    if _newTimeLock == _prevTimeLock:
        return False
    return _newTimeLock >= MIN_REG_TIME_LOCK and _newTimeLock <= MAX_REG_TIME_LOCK


# finish setup


@external
def setRegistryTimeLockAfterSetup(_numBlocks: uint256 = 0) -> bool:
    assert gov._canGovern(msg.sender) # dev: no perms
    assert self.registryChangeTimeLock == 0 # dev: already set

    timeLock: uint256 = _numBlocks
    if timeLock == 0:
        timeLock = MIN_REG_TIME_LOCK
    return self._setRegistryTimeLock(timeLock)


# utils


@view
@external
def minRegistryTimeLock() -> uint256:
    return MIN_REG_TIME_LOCK


@view
@external
def maxRegistryTimeLock() -> uint256:
    return MAX_REG_TIME_LOCK


#################
# Views / Utils #
#################


# is valid addr


@view
@external
def isValidAddr(_addr: address) -> bool:
    return self._isValidAddr(_addr)


@view
@internal
def _isValidAddr(_addr: address) -> bool:
    return self.addrToRegId[_addr] != 0


# is valid addr id


@view
@external
def isValidRegId(_regId: uint256) -> bool:
    return self._isValidRegId(_regId)


@view
@internal
def _isValidRegId(_regId: uint256) -> bool:
    return _regId != 0 and _regId < self.numAddrs


# get reg id


@view
@external
def getRegId(_addr: address) -> uint256:
    return self._getRegId(_addr)


@view
@internal
def _getRegId(_addr: address) -> uint256:
    return self.addrToRegId[_addr]


# get addr


@view
@external
def getAddr(_regId: uint256) -> address:
    return self._getAddr(_regId)


@view
@internal
def _getAddr(_regId: uint256) -> address:
    return self.addrInfo[_regId].addr


# get addr info


@view
@external
def getAddrInfo(_regId: uint256) -> AddressInfo:
    return self._getAddrInfo(_regId)


@view
@internal
def _getAddrInfo(_regId: uint256) -> AddressInfo:
    return self.addrInfo[_regId]


# get addr description


@view
@external
def getAddrDescription(_regId: uint256) -> String[64]:
    return self._getAddrDescription(_regId)


@view
@internal
def _getAddrDescription(_regId: uint256) -> String[64]:
    return self.addrInfo[_regId].description


# get num addrs


@view
@external
def getNumAddrs() -> uint256:
    return self._getNumAddrs()


@view
@internal
def _getNumAddrs() -> uint256:
    return self.numAddrs - 1


# get last addr


@view
@external
def getLastAddr() -> address:
    return self._getLastAddr()


@view
@internal
def _getLastAddr() -> address:
    lastRegId: uint256 = self.numAddrs - 1
    return self.addrInfo[lastRegId].addr


# get last reg id


@view
@external
def getLastRegId() -> uint256:
    return self._getLastRegId()


@view
@internal
def _getLastRegId() -> uint256:
    return self.numAddrs - 1

contracts/modules/Addys.vy.content:
#     Underscore Protocol License: https://github.com/underscore-finance/underscore-protocol/blob/master/LICENSE.md

# @version 0.4.3

interface UndyHq:
    def isValidAddr(_addr: address) -> bool: view
    def getAddr(_regId: uint256) -> address: view
    def undyToken() -> address: view

interface Switchboard:
    def isSwitchboardAddr(_addr: address) -> bool: view

interface LegoBook:
    def isLegoAddr(_addr: address) -> bool: view

struct Addys:
    hq: address
    undyToken: address
    ledger: address
    missionControl: address
    legoBook: address
    switchboard: address
    hatchery: address
    lootDistributor: address
    appraiser: address
    walletBackpack: address
    billing: address
    vaultRegistry: address

# hq
UNDY_HQ_FOR_ADDYS: immutable(address)

# core addys
LEDGER_ID: constant(uint256) = 1
MISSION_CONTROL_ID: constant(uint256) = 2
LEGO_BOOK_ID: constant(uint256) = 3
SWITCHBOARD_ID: constant(uint256) = 4
HATCHERY_ID: constant(uint256) = 5
LOOT_DISTRIBUTOR_ID: constant(uint256) = 6
APPRAISER_ID: constant(uint256) = 7
WALLET_BACKPACK_ID: constant(uint256) = 8
BILLING_ID: constant(uint256) = 9
VAULT_REGISTRY_ID: constant(uint256) = 10


@deploy
def __init__(_undyHq: address):
    assert _undyHq != empty(address) # dev: invalid undy hq
    UNDY_HQ_FOR_ADDYS = _undyHq


########
# Core #
########


@view
@external
def getAddys() -> Addys:
    return self._generateAddys()


@view
@internal
def _getAddys(_addys: Addys = empty(Addys)) -> Addys:
    if _addys.hq != empty(address):
        return _addys
    return self._generateAddys()


@view
@internal
def _generateAddys() -> Addys:
    hq: address = UNDY_HQ_FOR_ADDYS
    return Addys(
        hq = hq,
        undyToken = staticcall UndyHq(hq).undyToken(),
        ledger = staticcall UndyHq(hq).getAddr(LEDGER_ID),
        missionControl = staticcall UndyHq(hq).getAddr(MISSION_CONTROL_ID),
        legoBook = staticcall UndyHq(hq).getAddr(LEGO_BOOK_ID),
        switchboard = staticcall UndyHq(hq).getAddr(SWITCHBOARD_ID),
        hatchery = staticcall UndyHq(hq).getAddr(HATCHERY_ID),
        lootDistributor = staticcall UndyHq(hq).getAddr(LOOT_DISTRIBUTOR_ID),
        appraiser = staticcall UndyHq(hq).getAddr(APPRAISER_ID),
        walletBackpack = staticcall UndyHq(hq).getAddr(WALLET_BACKPACK_ID),
        billing = staticcall UndyHq(hq).getAddr(BILLING_ID),
        vaultRegistry = staticcall UndyHq(hq).getAddr(VAULT_REGISTRY_ID),
    )


##########
# Tokens #
##########


@view
@internal
def _getUndyToken() -> address:
    return staticcall UndyHq(UNDY_HQ_FOR_ADDYS).undyToken()


###########
# Helpers #
###########


# undy hq


@view
@external
def getUndyHq() -> address:
    return self._getUndyHq()


@view
@internal
def _getUndyHq() -> address:
    return UNDY_HQ_FOR_ADDYS


# utils


@view
@internal
def _isValidUndyAddr(_addr: address, _hq: address = empty(address)) -> bool:
    hq: address = _hq
    if _hq == empty(address):
        hq = UNDY_HQ_FOR_ADDYS

    # core departments
    if staticcall UndyHq(hq).isValidAddr(_addr):
        return True

    # lego book
    legoBook: address = staticcall UndyHq(hq).getAddr(LEGO_BOOK_ID)
    if legoBook != empty(address) and staticcall LegoBook(legoBook).isLegoAddr(_addr):
        return True

    # switchboard config
    switchboard: address = staticcall UndyHq(hq).getAddr(SWITCHBOARD_ID)
    if switchboard != empty(address) and staticcall Switchboard(switchboard).isSwitchboardAddr(_addr):
        return True

    return False


@view
@internal
def _isSwitchboardAddr(_addr: address) -> bool:
    switchboard: address = staticcall UndyHq(UNDY_HQ_FOR_ADDYS).getAddr(SWITCHBOARD_ID)
    if switchboard == empty(address):
        return False
    return staticcall Switchboard(switchboard).isSwitchboardAddr(_addr)


@view
@internal
def _isLegoBookAddr(_addr: address) -> bool:
    legoBook: address = staticcall UndyHq(UNDY_HQ_FOR_ADDYS).getAddr(LEGO_BOOK_ID)
    if legoBook == empty(address):
        return False
    return staticcall LegoBook(legoBook).isLegoAddr(_addr)


###############
